SET DEFINE OFF;DELETE FROM FLDMASTER WHERE 1 = 1 AND NVL(OBJNAME,'NULL') = NVL('0067','NULL');SELECT ''<$TAGFIELD>'' FILTERCD, ''ALL'' VALUE, ''ALL'' VALUECD, ''ALL''  DISPLAY, ''ALL'' EN_DISPLAY,''ALL'' DESCRIPTION
FROM  dual
Union
(SELECT FILTERCD, VALUE, VALUECD, DISPLAY, EN_DISPLAY, DESCRIPTION
FROM VW_CUSTODYCD_SUBACCOUNT_ACTIVE WHERE FILTERCD=''<$TAGFIELD>'')
) order by (case when value=''ALL'' then ''0'' else ''1''|| VALUE end)', 'N', 'P_ACCTNO', 'Y', '', 'N', '', '', '', 'N');FROM CFMAST CF, CFTYPE A
WHERE CF.CUSTODYCD=''<$TAGFIELD>'' AND CF.CUSTTYPE = A.CUSTTYPE AND A.isdefault =''Y''
union all
select ''<NULL>'' FILTERCD, ''<NULL>'' VALUE,''<NULL>'' VALUECD,''Không đổi'' DISPLAY,''Không đổi'' EN_DISPLAY,''Không đổi'' DESCRIPTION
FROM DUAL ', 'N', 'P_ACTYEN', 'Y', '', 'N', '', '', '', 'N');COMMIT;